mapper.insert:
      INSERT INTO COMMUNITY_COMMENT
      (
	     	COMMUNITY_COMMENT_NO
	      , NOTICE_CATEGORY
	      , NOTICE_NO
	      , NICKNAME
	      , ID
	      , COMMUNITY_COMMENT
	      , COMMUNITY_COMMENT_REGDATE

      ) VALUES (
      (SELECT
      IFNULL(MAX(COMMUNITY_COMMENT_NO), 0) + 1
      FROM
      		COMMUNITY_COMMENT ALIAS_FOR_SUBQUERY
      WHERE
      		NOTICE_CATEGORY =#{noticeCategory}
      AND
      		NOTICE_NO = #{noticeNo})
      , #{noticeCategory}
      , #{noticeNo}
      , #{nickname}
      , #{id}
      , #{communityComment}
      , NOW()
      )

mapper.select:

SELECT * FROM COMMUNITY_COMMENT
WHERE
		NOTICE_CATEGORY =#{noticeCategory}
AND
		NOTICE_NO = #{noticeNo}
ORDER BY COMMUNITY_COMMENT_NO ASC

mapper.delete:
DELETE FROM COMMUNITY_COMMENT
WHERE
		COMMUNITY_COMMENT_NO = #{communityCommentNo}
AND 
		NOTICE_CATEGORY =#{noticeCategory}
AND 
		NOTICE_NO = #{noticeNo}
