mapper.insert:
		INSERT INTO CUSTOMERSERVICE_COMMENT(
											QNA_COMMENT_NO
											,QNA_NO
											,QNA_COMMENT
											,NICKNAME
											,ID
											) VALUES(
											 (SELECT
											 IFNULL(MAX(QNA_COMMENT_NO), 0) + 1
   											  FROM
    										  CUSTOMERSERVICE_COMMENT ALIAS_FOR_SUBQUERY
    										  WHERE
    										  QNA_NO = #{qnaNo})
    										  , #{qnaNo}
    										  , #{qnaComment}
    										  , #{nickname}
    										  , #{id}
										)

mapper.select:
	SELECT * FROM CUSTOMERSERVICE_COMMENT
	WHERE QNA_NO = #{qnaNo}
	AND ID = #{id}
    ORDER BY QNA_COMMENT_NO ASC

mapper.delete:
DELETE FROM CUSTOMERSERVICE_COMMENT
WHERE QNA_COMMENT_NO = #{qnaCommentNo}
AND QNA_NO = #{qnaNo}
AND ID = #{id}
